import React, { useState } from 'react';
import axios from 'axios';
import './style.css';

const express = require('express');
const path = require('path');
const MATLAB = require('matlab-engine');

const app = express();
const port = 3001;

// Connect to MATLAB engine
const matlab = MATLAB.start();

// Define image processing route
app.post('/api/processImage/:algorithm', async (req, res) => {
  const file = req.files.file;
  const algorithm = req.params.algorithm;

  // Save image file temporarily
  const tmpImagePath = path.join(process.cwd(), 'tmp', file.name);
  file.mv(tmpImagePath);

  // Call MATLAB function for processing
  const processedImage = await matlab.eval('processImage(algorithm, "' + tmpImagePath + '")');

  // Send processed image back to React component
  res.send({ processedImage });

  // Delete temporary image file
  fs.unlinkSync(tmpImagePath);
});

// Start listening on port 3001
app.listen(port, () => console.log(`Server listening on port ${port}`));


const ImageProcessingPage = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [processedImage, setProcessedImage] = useState(null);

    // Xử lý chọn file
    const handleFileChange = (event) => {
        // Lưu file đã chọn vào state
        setSelectedFile(event.target.files[0]);
    };

    // Xử lý khi bắt đầu xử lý ảnh
    const handleImageProcessing = async (algorithm) => {
        try {
            const formData = new FormData();
            formData.append('file', selectedFile);

            // Gửi file và thuật toán lên server
            const response = await axios.post(`/api/processImage/${algorithm}`, formData);

            // Lưu ảnh đã xử lý vào state
            setProcessedImage(response.data.processedImage);
        } catch (error) {
            console.error('Error processing image:', error);
        }
    };

    return (
        <div className='background'>
            <h2>Trang xử lý ảnh</h2>
            <input type="file" onChange={handleFileChange} />
            <select onChange={(e) => handleImageProcessing(e.target.value)}>
                <option value="algorithm1">Thuật toán 1</option>
                <option value="algorithm2">Thuật toán 2</option>
                {/* Thêm các thuật toán khác nếu cần */}
            </select>
            <button>Bắt đầu</button>
            {/* Hiển thị ảnh trước và sau khi xử lý */}
            {selectedFile && (
                <div>
                    <h3>Ảnh trước khi xử lý</h3>
                    <img src={URL.createObjectURL(selectedFile)} alt="Before processing" />
                </div>
            )}
            {processedImage && (
                <div>
                    <h3>Ảnh sau khi xử lý</h3>
                    <img src={processedImage} alt="After processing" />
                </div>
            )}
        </div>
    );
};

export default ImageProcessingPage;
