// src/components/PatientInfo.js

import React, { useState } from 'react';

function PatientInfo({ onNext }) {
    const [patientData, setPatientData] = useState({
        name: '',
        dob: '',
        gender: '',
        phone: '',
        address: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPatientData({ ...patientData, [name]: value });
    };

    const handleNext = () => {
        onNext(patientData);
    };

    return (
        <div>
            <h2>Thông tin bệnh nhân</h2>
            <form>
                <div>
                    <label>Tên:</label>
                    <input type="text" name="name" value={patientData.name} onChange={handleChange} />
                </div>
                <div>
                    <label>Ngày sinh:</label>
                    <input type="date" name="dob" value={patientData.dob} onChange={handleChange} />
                </div>
                <div>
                    <label>Giới tính:</label>
                    <input type="text" name="gender" value={patientData.gender} onChange={handleChange} />
                </div>
                <div>
                    <label>Số điện thoại:</label>
                    <input type="text" name="phone" value={patientData.phone} onChange={handleChange} />
                </div>
                <div>
                    <label>Quê quán:</label>
                    <input type="text" name="address" value={patientData.address} onChange={handleChange} />
                </div>
            </form>
            <button onClick={handleNext}>Tiếp theo</button>
        </div>
    );
}

export default PatientInfo;
