import React from 'react';
import batdauImg from '.i/batdau.jpg'; // Đường dẫn đến ảnh batdau.jpg
import taianhImg from './taianh.jpg'; // Đường dẫn đến ảnh taianh.jpg

const InstructionsPage = () => {
    return (
        <div>
            <h2>Hướng dẫn sử dụng</h2>
            <p>B1: Chạy Project bằng câu lệnh "npm start".</p>
            <p>B2: Chọn qua mục bắt đầu để có thể bắt đầu sử dụng giao diện.</p>
            <img src={batdauImg} alt="Ảnh minh họa bắt đầu" />
            <p>B3: Ấn vào nút "Chọn tệp" và chọn ảnh của bạn sau đó ấn nút "Open" để có thể tải ảnh lên.</p>
            <img src={taianhImg} alt="Ảnh minh họa tải ảnh" />
            <p>B4: Ấn nút "Bắt đầu" để có thể xử lý ảnh.</p>
        </div>
    );
};

export default InstructionsPage;
