import React, { useState } from 'react';
import axios from 'axios';
import './style.css';

const ImageProcessingPage = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [processedImage, setProcessedImage] = useState(null);

    // Xử lý chọn file
    const handleFileChange = (event) => {
        // Lưu file đã chọn vào state
        setSelectedFile(event.target.files[0]);
    };

    // Xử lý khi bắt đầu xử lý ảnh
    const handleImageProcessing = async () => {
        if (selectedFile) {
            try {
                const formData = new FormData();
                formData.append('file', selectedFile);

                // Gửi file và yêu cầu xử lý lên server
                const response = await axios.post('/api/processImage', formData);

                // Lưu ảnh đã xử lý vào state
                setProcessedImage(response.data.processedImage);
            } catch (error) {
                console.error('Error processing image:', error);
            }
        } else {
            console.log('Vui lòng chọn một file trước khi bắt đầu xử lý.');
        }
    };

    return (
        <div className='background'>
            <div>

            </div>
            
        </div>
    );
};

export default ImageProcessingPage;
