// src/App.js

import React, { useState } from 'react';
import './App.css';
import PatientInfo from './components/PatientInfo';
import ImageUpload from './components/ImageUpload';
import Result from './components/Result';

function App() {
  const [step, setStep] = useState(1);
  const [patientData, setPatientData] = useState({});
  const [uploadedImage, setUploadedImage] = useState('');

  const handleNext = (data) => {
    setPatientData(data);
    setStep(2);
  };

  const handleUpload = (image) => {
    setUploadedImage(image);
    setStep(3);
  };

  const handleSave = () => {
    // Gửi thông tin bệnh nhân và ảnh lên máy chủ hoặc lưu trữ tại đây
    // Sau khi lưu, bạn có thể thực hiện các xử lý y tế bổ sung
    alert('Đã lưu kết quả.');
  };

  return (
    <div className="App">
      {step === 1 && <PatientInfo onNext={handleNext} />}
      {step === 2 && <ImageUpload onUpload={handleUpload} />}
      {step === 3 && <Result patientData={patientData} uploadedImage={uploadedImage} onSave={handleSave} />}
    </div>
  );
}

export default App;
